const fs = require('fs');
const WEIGHT_FILE = require('./index').WEIGHT_FILE

function teardown() {
  // remove file
  try {
    fs.unlinkSync(WEIGHT_FILE)
  } catch (err) {
    if (err.code == 'ENOENT') {
      // no worries, already removed
    } else {
      console.error(err)
    }
  }
}

module.exports = teardown;
